package Software_02;

public class SpecialCharEx_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('\'');				// ' 출력 후 줄바꿈
		System.out.println("abc\t123\b456");	// abc+탭 간격+12출력 백슬래시 활용하여 3 제외하고 456 출력 후 줄바꿈
		System.out.println('\n');				// 개행문자(줄바꿈) 출력하고 줄바꿈
		System.out.println("\"Hello\"");		// "Hello" 출력하고 줄바꿈
		System.out.println("c:\\");				// c:\ 출력 후 줄바꿈
	}
}
